{
  description = "SkyPilot - Run LLMs and AI on any Cloud";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
    treefmt-nix.url = "github:numtide/treefmt-nix";
  };

  outputs = { self, nixpkgs, flake-utils, treefmt-nix }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};

        treefmtEval = treefmt-nix.lib.evalModule pkgs {
          projectRootFile = "flake.nix";
          programs = {
            nixpkgs-fmt.enable = true;
            black.enable = true;
            isort.enable = true;
          };
        };

        skypilot = pkgs.python3Packages.buildPythonApplication rec {
          pname = "skypilot";
          version = "0.9.3";

          src = pkgs.fetchFromGitHub {
            owner = "skypilot-org";
            repo = "skypilot";
            tag = "v0.9.3";
            hash = "sha256-iKNvzGiKM4QSG25CusZ1YRIou010uWyMLEAaFIww+FA=";
          };

          pyproject = true;

          build-system = with pkgs.python3Packages; [ setuptools ];

          propagatedBuildInputs = with pkgs.python3Packages; [
            aiofiles
            cachetools
            click
            colorama
            cryptography
            fastapi
            filelock
            httpx
            jinja2
            jsonschema
            networkx
            packaging
            pandas
            pendulum
            prettytable
            psutil
            pydantic
            python-dotenv
            python-multipart
            pyyaml
            pulp
            requests
            rich
            setproctitle
            tabulate
            typing-extensions
            uvicorn
            wheel
          ];

          meta = {
            description = "Run LLMs and AI on any Cloud";
            longDescription = ''
              SkyPilot is a framework for running LLMs, AI, and batch jobs on any
              cloud, offering maximum cost savings, highest GPU availability, and
              managed execution.
            '';
            homepage = "https://github.com/skypilot-org/skypilot";
            license = pkgs.lib.licenses.asl20;
            maintainers = with pkgs.lib.maintainers; [ seanrmurphy ];
            mainProgram = "sky";
          };
        };
      in
      {
        packages = {
          default = skypilot;
          skypilot = skypilot;
        };

        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            python3
            python3Packages.pip
            python3Packages.setuptools
            python3Packages.wheel
            # Development tools
            python3Packages.black
            python3Packages.flake8
            python3Packages.pytest
            python3Packages.mypy
            # SkyPilot dependencies
            skypilot
          ];

          shellHook = ''
            echo "SkyPilot development environment"
            echo "Available commands:"
            echo "  sky --help    - SkyPilot CLI"
            echo "  python        - Python interpreter"
            echo "  pip           - Package installer"
            echo "  black         - Code formatter"
            echo "  flake8        - Linter"
            echo "  pytest        - Test runner"
            echo "  mypy          - Type checker"
          '';
        };

        formatter = treefmtEval.config.build.wrapper;
      });
}

