      description = "The SkyPilot package to use.";
    };

    user = mkOption {
      type = types.str;
      default = "skypilot";
      description = "User account under which SkyPilot runs.";
    };

    group = mkOption {
      type = types.str;
      default = "skypilot";
      description = "Group under which SkyPilot runs.";
    };

    configDir = mkOption {
