          port = mkOption {
            type = types.port;
            default = 8080;
            description = "Port for the SkyPilot web UI.";
          };

          host = mkOption {
            type = types.str;
            default = "127.0.0.1";
            description = "Host address to bind the web UI to.";
          };

          openFirewall = mkOption {
            type = types.bool;
            default = false;
            description = "Open firewall for the web UI port.";
          };
        };
      };
      description = "Web UI configuration options.";
    };

