# SkyPilot NixOS Module
# 
# This module provides comprehensive SkyPilot integration for NixOS systems,
# including service management, configuration generation, and cloud provider setup.
#
# Maintainers: [ ]
# Documentation: ./skypilot.md

{ config, lib, pkgs, ... }:

with lib;
with lib.lists;

let
  cfg = config.services.skypilot;

  # Use the SkyPilot package from the overlay or build it locally
  skypilot-pkg =
    if pkgs ? skypilot
    then pkgs.skypilot
    else
      pkgs.python3Packages.buildPythonApplication rec {
        pname = "skypilot";
        version = "0.9.3";

        src = pkgs.fetchFromGitHub {
          owner = "skypilot-org";
          repo = "skypilot";
          tag = "v${version}";
          hash = "sha256-iKNvzGiKM4QSG25CusZ1YRIou010uWyMLEAaFIww+FA=";
        };

        pyproject = true;
        build-system = with pkgs.python3Packages; [ setuptools ];

        propagatedBuildInputs = with pkgs.python3Packages; [
          aiofiles
          cachetools
          click
          colorama
          cryptography
          fastapi
          filelock
          httpx
          jinja2
          jsonschema
          networkx
          packaging
          pandas
          pendulum
          prettytable
          psutil
          pydantic
          python-dotenv
          python-multipart
          pyyaml
          pulp
          requests
          rich
          setproctitle
          tabulate
          typing-extensions
          uvicorn
          wheel
        ];

        meta = {
          description = "Run LLMs and AI on any Cloud";
          homepage = "https://github.com/skypilot-org/skypilot";
          license = pkgs.lib.licenses.asl20;
          mainProgram = "sky";
        };
      };

  # Configuration file for SkyPilot
  skypilotConfig = pkgs.writeText "skypilot-config.yaml" (generators.toYAML { } cfg.config);

  # Environment script
  skypilotEnv = pkgs.writeShellScript "skypilot-env" ''
    export SKYPILOT_CONFIG_DIR=${cfg.configDir}
    export SKYPILOT_LOGS_DIR=${cfg.logsDir}
    export SKYPILOT_CACHE_DIR=${cfg.cacheDir}
    ${optionalString (cfg.config != { }) ''
      export SKYPILOT_CONFIG=${skypilotConfig}
    ''}
    ${cfg.extraEnvironment}
  '';

in
