    };

    systemdServices = mkOption {
      type = types.bool;
      default = true;
      description = "Create systemd services for SkyPilot daemon processes.";
    };

    enableSpotInstances = mkOption {
      type = types.bool;
      default = false;
      description = "Enable spot instance support by default.";
    };

    monitoring = mkOption {
      type = types.submodule {
        options = {
          enable = mkOption {
            type = types.bool;
            default = false;
            description = "Enable SkyPilot monitoring and metrics collection.";
          };

          metricsPort = mkOption {
            type = types.port;
            default = 9090;
            description = "Port for metrics endpoint.";
          };

          logLevel = mkOption {
            type = types.enum [ "DEBUG" "INFO" "WARNING" "ERROR" "CRITICAL" ];
            default = "INFO";
            description = "Log level for SkyPilot services.";
          };
        };
      };
      description = "Monitoring and logging configuration.";
    };
  };

  config = mkIf cfg.enable {
    # Create user and group
    users.users.${cfg.user} = {
      isSystemUser = true;
      group = cfg.group;
      home = cfg.configDir;
      createHome = true;
      description = "SkyPilot service user";
    };

    users.groups.${cfg.group} = { };

    # Create necessary directories
    systemd.tmpfiles.rules = [
      "d ${cfg.configDir} 0755 ${cfg.user} ${cfg.group} -"
      "d ${cfg.logsDir} 0755 ${cfg.user} ${cfg.group} -"
      "d ${cfg.cacheDir} 0755 ${cfg.user} ${cfg.group} -"
    ] ++ (mapAttrsToList
      (cloud: credFile:
        "L+ ${cfg.configDir}/${cloud}-credentials 0600 ${cfg.user} ${cfg.group} - ${credFile}"
      )
      cfg.cloudCredentials);

    # Install SkyPilot package system-wide
    environment.systemPackages = [ cfg.package ];

    # Environment variables for all users
    environment.variables = {
      SKYPILOT_CONFIG_DIR = cfg.configDir;
      SKYPILOT_LOGS_DIR = cfg.logsDir;
      SKYPILOT_CACHE_DIR = cfg.cacheDir;
    } // (optionalAttrs (cfg.config != { }) {
      SKYPILOT_CONFIG = toString skypilotConfig;
    });

    # Web UI service
    systemd.services.skypilot-web-ui = mkIf cfg.enableWebUI {
      description = "SkyPilot Web UI";
      wantedBy = [ "multi-user.target" ];
      after = [ "network.target" ];
      serviceConfig = {
        Type = "simple";
        User = cfg.user;
        Group = cfg.group;
        ExecStartPre = "${skypilotEnv}";
        ExecStart = "${cfg.package}/bin/sky serve up --host ${cfg.webUI.host} --port ${toString cfg.webUI.port}";
        Restart = "always";
        RestartSec = "10s";
        WorkingDirectory = cfg.configDir;
        StandardOutput = "journal";
        StandardError = "journal";
      };
      environment = {
        SKYPILOT_CONFIG_DIR = cfg.configDir;
        SKYPILOT_LOGS_DIR = cfg.logsDir;
        SKYPILOT_CACHE_DIR = cfg.cacheDir;
      };
    };

    # Cluster management service
    systemd.services.skypilot-cluster-manager = mkIf (cfg.enableCluster && cfg.systemdServices) {
      description = "SkyPilot Cluster Manager";
      wantedBy = [ "multi-user.target" ];
      after = [ "network.target" ];
      serviceConfig = {
        Type = "simple";
        User = cfg.user;
        Group = cfg.group;
        ExecStartPre = "${skypilotEnv}";
        ExecStart = "${cfg.package}/bin/sky status --refresh";
        Restart = "always";
        RestartSec = "300s"; # Check every 5 minutes
        WorkingDirectory = cfg.configDir;
        StandardOutput = "journal";
        StandardError = "journal";
      };
      environment = {
        SKYPILOT_CONFIG_DIR = cfg.configDir;
        SKYPILOT_LOGS_DIR = cfg.logsDir;
        SKYPILOT_CACHE_DIR = cfg.cacheDir;
      };
    };

    # Monitoring service
    systemd.services.skypilot-monitor = mkIf cfg.monitoring.enable {
      description = "SkyPilot Monitoring Service";
      wantedBy = [ "multi-user.target" ];
      after = [ "network.target" ];
      serviceConfig = {
        Type = "simple";
        User = cfg.user;
        Group = cfg.group;
        ExecStartPre = "${skypilotEnv}";
        ExecStart = "${pkgs.python3}/bin/python -m http.server ${toString cfg.monitoring.metricsPort}";
        Restart = "always";
        RestartSec = "30s";
        WorkingDirectory = cfg.logsDir;
        StandardOutput = "journal";
        StandardError = "journal";
      };
      environment = {
        SKYPILOT_LOG_LEVEL = cfg.monitoring.logLevel;
        SKYPILOT_CONFIG_DIR = cfg.configDir;
        SKYPILOT_LOGS_DIR = cfg.logsDir;
        SKYPILOT_CACHE_DIR = cfg.cacheDir;
      };
    };

    # Firewall configuration
    networking.firewall.allowedTCPPorts =
      (optionals (cfg.enableWebUI && cfg.webUI.openFirewall) [ cfg.webUI.port ]) ++
      (optionals cfg.monitoring.enable [ cfg.monitoring.metricsPort ]);

    # Auto-stop timer for clusters
    systemd.timers.skypilot-autostop = mkIf (cfg.enableCluster && cfg.cluster.autoStop != null) {
      description = "SkyPilot Auto-stop Timer";
      wantedBy = [ "timers.target" ];
      timerConfig = {
        OnBootSec = "10min";
        OnUnitActiveSec = "${toString cfg.cluster.autoStop}min";
        Persistent = true;
      };
    };

    systemd.services.skypilot-autostop = mkIf (cfg.enableCluster && cfg.cluster.autoStop != null) {
      description = "SkyPilot Auto-stop Service";
      serviceConfig = {
        Type = "oneshot";
        User = cfg.user;
        Group = cfg.group;
        ExecStartPre = "${skypilotEnv}";
        ExecStart = "${cfg.package}/bin/sky autostop --all --idle ${toString cfg.cluster.autoStop}";
        WorkingDirectory = cfg.configDir;
      };
      environment = {
        SKYPILOT_CONFIG_DIR = cfg.configDir;
        SKYPILOT_LOGS_DIR = cfg.logsDir;
        SKYPILOT_CACHE_DIR = cfg.cacheDir;
      };
    };

    # Note: Sudo rules for SkyPilot can be configured manually if needed
    # security.sudo.rules = [
    #   {
    #     users = [ cfg.user ];
    #     commands = [
    #       {
    #         command = "${cfg.package}/bin/sky";
    #         options = [ "NOPASSWD" ];
    #       }
    #     ];
    #   }
    # ];

    # Kernel modules for container support
    boot.kernelModules = mkIf cfg.enableCluster [ "overlay" "br_netfilter" ];

    # System-wide SkyPilot configuration
    environment.etc."skypilot/config.yaml" = mkIf (cfg.config != { }) {
      text = generators.toYAML { } (cfg.config //
        (optionalAttrs cfg.enableCluster (
          let
            clusterConfig =
              (optionalAttrs (cfg.cluster.defaultInstanceType != null) {
                default_instance_type = cfg.cluster.defaultInstanceType;
              }) //
              (optionalAttrs (cfg.cluster.defaultRegion != null) {
                default_region = cfg.cluster.defaultRegion;
              });
          in
          optionalAttrs (clusterConfig != { }) {
            cluster = clusterConfig;
          }
        )) //
        (optionalAttrs cfg.enableSpotInstances {
          spot = {
            enabled = true;
          };
        })
      );
      mode = "0644";
    };

    # Assertions
    assertions = [
      {
        assertion = cfg.enableWebUI -> cfg.webUI.port > 0;
        message = "SkyPilot web UI port must be greater than 0";
      }
      {
        assertion = cfg.monitoring.enable -> cfg.monitoring.metricsPort > 0;
        message = "SkyPilot monitoring metrics port must be greater than 0";
      }
      {
        assertion = !cfg.enableCluster || cfg.cluster.autoStop == null || cfg.cluster.autoStop > 0;
        message = "SkyPilot auto-stop time must be greater than 0 minutes";
      }
    ];
  };

  meta = {
    maintainers = with lib.maintainers; [ ];
    doc = ./skypilot.md;
  };
}

# SkyPilot NixOS Module

This module provides a comprehensive NixOS integration for SkyPilot, a framework for running LLMs, AI, and batch jobs on any cloud.

## Features

- **Complete SkyPilot Installation**: Installs and configures SkyPilot with all dependencies
- **User & Group Management**: Creates dedicated system user and group for SkyPilot
- **Directory Management**: Sets up configuration, logging, and cache directories
- **Systemd Services**: Optional systemd services for cluster management and monitoring
- **Web UI Support**: Optional web interface for SkyPilot management
- **Cloud Credentials**: Secure handling of cloud provider credentials
- **Monitoring**: Built-in monitoring and metrics collection
- **Security Hardening**: Proper permissions and optional sudo rules
- **Firewall Integration**: Automatic firewall configuration for services

## Basic Configuration

```nix
{
  services.skypilot = {
    enable = true;
  };
}
```

## Advanced Configuration

```nix
{
  services.skypilot = {
    enable = true;
    enableWebUI = true;
    enableCluster = true;
    systemdServices = true;

    # Custom user and directories
    user = "skypilot";
    group = "skypilot";
    configDir = "/var/lib/skypilot/config";
    logsDir = "/var/log/skypilot";
    cacheDir = "/var/cache/skypilot";

    # Web UI configuration
    webUI = {
      port = 8080;
      host = "0.0.0.0";
      openFirewall = true;
    };

    # Cluster management
    cluster = {
      autoStop = 60; # minutes
      defaultInstanceType = "m5.large";
      defaultRegion = "us-west-2";
    };

    # Monitoring
    monitoring = {
      enable = true;
      metricsPort = 9090;
      logLevel = "INFO";
    };

    # SkyPilot configuration
    config = {
      cloud = {
        aws = {
          region = "us-west-2";
        };
        gcp = {
          project = "my-project";
          zone = "us-central1-a";
        };
      };
      spot = {
        enabled = true;
        max_price = 1.0;
      };
    };

    # Cloud credentials
    cloudCredentials = {
      aws = "/run/secrets/aws-credentials";
      gcp = "/run/secrets/gcp-service-account.json";
    };

    # Extra environment variables
    extraEnvironment = ''
      export AWS_PROFILE=skypilot
      export GOOGLE_APPLICATION_CREDENTIALS=/run/secrets/gcp-service-account.json
    '';
  };
}
```

## Configuration Options

### Core Options

- `enable`: Enable the SkyPilot service
- `package`: SkyPilot package to use
- `user`/`group`: System user and group for SkyPilot
- `configDir`/`logsDir`/`cacheDir`: Directory paths for SkyPilot data

### Web UI Options

- `enableWebUI`: Enable the web interface
- `webUI.port`: Web UI port (default: 8080)
- `webUI.host`: Bind address (default: 127.0.0.1)
- `webUI.openFirewall`: Open firewall for web UI

### Cluster Management

- `enableCluster`: Enable cluster management features
- `cluster.autoStop`: Auto-stop idle clusters (minutes)
- `cluster.defaultInstanceType`: Default instance type
- `cluster.defaultRegion`: Default cloud region

### Monitoring

- `monitoring.enable`: Enable monitoring service
- `monitoring.metricsPort`: Metrics endpoint port
- `monitoring.logLevel`: Logging level

### Security

- `cloudCredentials`: Paths to cloud credential files
- `extraEnvironment`: Additional environment variables
- `systemdServices`: Enable systemd service management

## Systemd Services

When `systemdServices = true`, the following services are created:

1. **skypilot-web-ui**: Web interface service (if `enableWebUI = true`)
2. **skypilot-cluster-manager**: Cluster status monitoring
3. **skypilot-monitor**: Metrics and monitoring service
4. **skypilot-autostop**: Auto-stop timer for idle clusters

## Cloud Provider Setup

### AWS

```nix
{
  services.skypilot = {
    cloudCredentials.aws = "/run/secrets/aws-credentials";
    config.cloud.aws = {
      region = "us-west-2";
    };
    extraEnvironment = ''
      export AWS_PROFILE=default
    '';
  };
}
```

### Google Cloud Platform

```nix
{
  services.skypilot = {
    cloudCredentials.gcp = "/run/secrets/gcp-service-account.json";
    config.cloud.gcp = {
      project = "my-project";
      zone = "us-central1-a";
    };
    extraEnvironment = ''
      export GOOGLE_APPLICATION_CREDENTIALS=/run/secrets/gcp-service-account.json
    '';
  };
}
```

### Azure

```nix
{
  services.skypilot = {
    cloudCredentials.azure = "/run/secrets/azure-credentials";
    config.cloud.azure = {
      region = "eastus";
    };
  };
}
```

## Security Considerations

1. **User Isolation**: SkyPilot runs under a dedicated system user
2. **File Permissions**: Configuration and credential files have restricted permissions
3. **Firewall Integration**: Only specified ports are opened
4. **Credential Management**: Cloud credentials are symlinked with proper permissions
5. **Sudo Rules**: Optional sudo access for cluster management operations

## Testing

The module includes comprehensive NixOS VM tests that verify:

- Basic installation and configuration
- Service functionality
- Web UI setup
- Monitoring services
- Custom configurations
- Security and permissions
- SkyPilot command functionality

Run tests with:

```bash
nix build .#checks.x86_64-linux.skypilot-module
```

## Troubleshooting

### Common Issues

1. **Service fails to start**: Check logs with `journalctl -u skypilot-*`
2. **Permission denied**: Verify file permissions in config directories
3. **Cloud access**: Ensure credentials are properly configured
4. **Network issues**: Check firewall rules and port bindings

### Log Locations

- System logs: `journalctl -u skypilot-*`
- SkyPilot logs: `/var/log/skypilot/`
- Configuration: `/var/lib/skypilot/config/`

## Examples

See the test configurations in `tests/skypilot-module.nix` for complete examples of different deployment scenarios.

